mapper.select:
select count(*)
  from tb_history
 where user_no = #{userNo}

   and connect_time between (SELECT date_add(now(),INTERVAL -#{rDays} DAY) FROM dual) and now();


   and connect_time between #{beginDate} and #{endDate}

SELECT A.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS ROWNUM, tb_history.* FROM (select (select code_info from tb_code where h.act_code = code_no) as act_name, connect_time, ip_addr, (select room_name from tb_room where h.room_no = room_no) as room_name, (select u.user_name from tb_user as u where u.user_no = h.user_no) as user_name, (select u.user_email from tb_user as u where u.user_no = h.user_no) as user_email from tb_history h where room_no in (select room_no from tb_room where user_no = #{userNo})
and connect_time between (SELECT date_add(now(),INTERVAL -#{rDays} DAY) FROM dual) and now()
and connect_time between #{beginDate} and #{endDate}
order by connect_time desc) tb_history, (SELECT @ROWNUM := 0) R ) A WHERE A.ROWNUM between #{begin} and #{end};
