mapper.select:
select room_no
  from tb_room
  where room_join_code = #{roomJoinCode}

mapper.update:
update tb_room_member

   set room_connect_code='bb11'


   set room_connect_code='bb12'

 where user_no=#{userNo}
   and room_no=#{roomNo}

mapper.select:
select *
  from tb_room
 where user_no = #{userNo}
   and room_del_state = #{roomDelState}

   and room_name = #{roomName}

select count(*)
  from tb_room
 where user_no = #{userNo}

mapper.update:

update tb_room
   set room_del_state = #{roomDelState}
 where room_no = #{roomNo}

mapper.select:
select count(*)
  from tb_room
 where room_no = #{roomNo}
   and room_password = #{roomPassword}

mapper.update:
update tb_room
   set 
	   room_info  = #{roomInfo},
       room_password =#{roomPassword}, 
       room_name = #{roomName}
 where room_no = #{roomNo}

update tb_room_member
   set room_connect_code = #{roomConnectCode}
 where room_no = #{roomNo} 
   and user_no = #{userNo}

mapper.select:
select room_name
  from tb_room
 where room_join_code = #{roomJoinCode}

mapper.update:

update tb_room_member
   set room_connect_code = #{roomConnectCode}
 where user_no = ${userNo}
   and room_no = (select room_no 
   		  from tb_room
   		 where room_join_code = #{roomJoinCode})

mapper.insert:
	 insert into tb_history(
            user_no, ip_addr, act_code, room_no
        )values(
            #{userNo}, #{ipAddr}, #{actName}, #{roomNo}
        )
